import React from "react";
import {Button, Text, TextInput, ToastAndroid,ScrollView, View} from "react-native";
import {styles} from "./styles"
import AsyncStorage from '@react-native-async-storage/async-storage';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import {timeoutPromise, refreshToken,getRaceList,changeWheelSet} from "./tools";
import {get_Dict_WheelOrder, getDropdown,getWheelSetInformation,getOrderDropdown,getWheelInformations} from "./tools_get_wheels";

export default class NewOrderScreen extends React.Component {
   constructor(props) {
        super(props);
        this.state = {
            setID:0,
            setData:[],
            list_wheels : [],
            dictButtons:[],
            dropdownlist : [[],[],[],[],[],[],[]],
            ButtonsList : ['Slicks Cold','Slicks Medium', 'Slicks Hot', 'Inters Intermediate', 'Rain DryWet', 'Rain HeavyWet'],
        }
    }

     changeRace = event => {
        this.props.navigation.goBack();
    }

     async getTabularData() {
        const accesstoken = await AsyncStorage.getItem('accesstoken');
        const raceID = await AsyncStorage.getItem('raceID');
        await getWheelInformations(accesstoken, raceID).then(Tab => {
            this.setState({list_wheels: Tab});
        }).catch(function (error) {
            console.log(error);
        })
    }

    async getDropdownData() {
        const accesstoken = await AsyncStorage.getItem('accesstoken');
        const raceID = await AsyncStorage.getItem('raceID');
        await getOrderDropdown(accesstoken, raceID).then(Tab => {
            this.setState({dropdownlist: Tab});
        }).catch(function (error) {
            console.log(error);
        })
    }

    getchoosenSetData(){
       let setData = [];
       this.state.list_wheels.forEach( function (element,index){if(element.setid==this.state.setID){this.setState({setData:element});}});
       console.log(this.state.setData);
    }

     async componentDidMount(){
        const accesstoken = await AsyncStorage.getItem('accesstoken');
        const raceID = await AsyncStorage.getItem('raceID');
        console.log(raceID)
        await this.getTabularData();
        console.log(this.state.list_wheels)
        await this.getDropdownData();
        console.log(this.state.dropdownlist)
    }

     renderTableHeader(number) {
         let header = ['Slicks Cold', 'Slicks Medium', 'Slicks Hot', 'Inters Intermediate', 'Rain DryWet', 'Rain HeavyWet'];
         let headerOrder = ['Bezeichnung', 'Datum und Uhrzeit', 'Kategorie', 'Status',
            'Kaltdruck', 'Bleed', 'Heizdaten', 'Warmdruck', 'Target Warmdruck', 'Bleed', 'Reifen ID'];
         if (number ==1){
             return header.map((key, index) => {
                 return <th bgcolor='#696969'
                            style={{textAlign: "left", padding: '8px', color: 'white', fontFamily: 'arial'}}
                            key={index}>{key.toUpperCase()}</th>
             })
         }
         if (number ==2){
             return headerOrder.map((key, index) => {
                 return <th bgcolor='#696969'
                            style={{textAlign: "left", padding: '8px', color: 'white', fontFamily: 'arial'}}
                            key={index}>{key.toUpperCase()}</th>
             })
         }
     }

     renderTableHeaderChoosen() {
         let header = ['Slicks Cold', 'Slicks Medium', 'Slicks Hot', 'Inters Intermediate', 'Rain DryWet', 'Rain HeavyWet'];
         return header.map((key, index) => {
             return <th bgcolor='#696969'
                        style={{textAlign: "left", padding: '8px', color: 'white', fontFamily: 'arial'}}
                        key={index}>{key.toUpperCase()}</th>
         })
     }

     renderTableOrderCat(){
       const optiondropdown1 = this.state.dropdownlist[0].map(v => (
            <option value={v.id} key={v.id}>{v.name}</option>
       ));
       const optiondropdown2 = this.state.dropdownlist[1].map(v => (
            <option value={v.id} key={v.id}>{v.name}</option>
       ));
       const optiondropdown3 = this.state.dropdownlist[2].map(v => (
            <option value={v.id} key={v.id}>{v.name}</option>
       ));
       const optiondropdown4 = this.state.dropdownlist[3].map(v => (
            <option value={v.id} key={v.id}>{v.name}</option>
       ));
       const optiondropdown5 = this.state.dropdownlist[4].map(v => (
            <option value={v.id} key={v.id}>{v.name}</option>
       ));
       const optiondropdown6 = this.state.dropdownlist[5].map(v => (
            <option value={v.id} key={v.id}>{v.name}</option>
       ));
       const coloumns = ['all'];
       return coloumns.map((buttons, index) => {
            return (
                <tr bgcolor='#696969' style={{textAlign: "left", padding: '8px', color: 'white', fontFamily: 'arial'}}
                    key={'1Tabelle'}>
                    <td>
                    <Dropdown options={optiondropdown1} onChange={this._onSelect} id ={optiondropdown1.id} value={optiondropdown1.name} placeholder="Whle ein Set aus" />
                    </td>
                    <td>
                    <Dropdown options={optiondropdown2} onChange={this._onSelect} id ={optiondropdown2.id} value={optiondropdown2.name} placeholder="Whle ein Set aus" />
                    </td>
                    <td>
                    <Dropdown options={optiondropdown3} onChange={this._onSelect} id ={optiondropdown3.id} value={optiondropdown3.name} placeholder="Whle ein Set aus" />
                    </td>
                    <td>
                    <Dropdown options={optiondropdown4} onChange={this._onSelect} id ={optiondropdown4.id} value={optiondropdown4.name} placeholder="Whle ein Set aus" />
                    </td>
                    <td>
                    <Dropdown options={optiondropdown5} onChange={this._onSelect} id ={optiondropdown5.id} value={optiondropdown5.name} placeholder="Whle ein Set aus" />
                    </td>
                    <td>
                    <Dropdown options={optiondropdown6} onChange={this._onSelect} id ={optiondropdown6.id} value={optiondropdown6.name} placeholder="Whle ein Set aus" />
                    </td>
                </tr>
            )})
     }

     renderTableData() {
        console.log(this.state.list_wheels)
        return this.state.list_wheels.map((list_wheels, index) => {
            return (
                <tr bgcolor='#696969' style={{textAlign: "left", padding: '8px', color: 'white', fontFamily: 'arial'}}
                    key={'renderTabelle12'}>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 100, padding: '8px'}}>
                        <input
                            id={list_wheels.setid} placeholder={'Setbezeichnung'} value={list_wheels.description}
                            name={'description'} onChange={this.save_changes_wheelSet}/>
                    </td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 100, padding: '8px'}}>
                        <input
                            id={list_wheels.setid} placeholder={'Datum und Uhrzeit'}
                            value={list_wheels.order_start} name={'order_start'} onChange={this.save_changes_wheelSet}/>
                    </td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 100, padding: '8px'}}>
                        <input id={list_wheels.setid} placeholder={'Kategorie'} value={list_wheels.cat} name={'cat'}
                               onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.setid} placeholder={'Unterkategorie'} value={list_wheels.subcat}
                               name={'subcat'} onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.setid} placeholder={'Bearbeitungsvariante'}
                               value={list_wheels.variant} name ={'variant'} onChange={this.save_changes_wheelSet}/>
                    </td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 100, padding: '8px'}}>
                        <input
                            id={list_wheels.setid} placeholder={list_wheels.status} value={list_wheels.status}
                            name={'status'} onChange={this.save_changes_wheelSet}/>
                    </td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 100, padding: '8px'}}>
                        <input id={list_wheels.setid} placeholder={'Felgentemperatur'}
                               value={list_wheels.temp_air} name={'temp_air'} onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.fl_id} placeholder={'Kaltdruck FL'} value={list_wheels.fl_pressure}
                               name={'pressure'} className={'fl_pressure'} onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.fr_id} placeholder={'Kaltdruck FR'} value={list_wheels.fr_pressure}
                               name={'pressure'} className={'fr_pressure'} onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.bl_id} placeholder={'Kaltdruck BL'} value={list_wheels.bl_pressure}
                               name={'pressure'} className={'bl_pressure'} onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.br_id} placeholder={'Kaltdruck BR'} value={list_wheels.br_pressure}
                               name={'pressure'} className={'fl_pressure'} onChange={this.save_changes_wheel}/>
                    </td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 150, padding: '8px'}}>
                        <input id={list_wheels.setid} placeholder={'bleed initial'}
                               value={list_wheels.bleed_initial} name={'bleed_initial'}
                               onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.setid} placeholder={'bleed hot'} value={list_wheels.bleed_hot}
                               name={'bleed_hot'} onChange={this.save_changes_wheelSet}/></td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 150, padding: '8px'}}
                        onChange={this.save_changes_wheelSet}>
                        <input id={list_wheels.setid} placeholder={'Heiztemperatur'} value={list_wheels.temp_heat}
                               name={'temp_heat'} onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.setid} placeholder={'Heizdauer'} value={list_wheels.heat_duration}
                               name={'heat_duration'} onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.setid} placeholder={'Heizstart'} value={list_wheels.heat_start}
                               name={'heat_start'} onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.setid} placeholder={'Heizende'} value={list_wheels.heat_end}
                               name={'heat_end'} onChange={this.save_changes_wheelSet}/></td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 100, padding: '8px'}}>
                        <input id={list_wheels.setid} placeholder={'Zeit der Messung'}
                               value={list_wheels.heat_press_timestamp} name={'heat_press_timestamp'}
                               onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.fl_id} placeholder={'Warmdruck FL'}
                               value={list_wheels.fl_hot_air_press} className={'fl_hot_air_press'} name={'hot_air_press'}
                               onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.fr_id} placeholder={'Warmdruck FR'}
                               value={list_wheels.fr_hot_air_press} className={'fr_hot_air_press'} name={'hot_air_press'}
                               onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.bl_id} placeholder={'Warmdruck BL'}
                               value={list_wheels.bl_hot_air_press} className={'bl_hot_air_press'} name={'hot_air_press'}
                               onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.br_id} placeholder={'Warmdruck BR'}
                               value={list_wheels.br_hot_air_press} className={'br_hot_air_press'} name={'hot_air_press'}
                               onChange={this.save_changes_wheel}/>
                    </td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 100, padding: '8px'}}>
                        <input id={list_wheels.setid} placeholder={'Target vorne'}
                               value={list_wheels.heat_press_front} name={'heat_press_front'}
                               onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.setid} placeholder={'Target hinten'}
                               value={list_wheels.heat_press_back} name={'heat_press_back'}
                               onChange={this.save_changes_wheelSet}/>
                    </td>
                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 100, padding: '8px'}}>
                        <input id={list_wheels.setid} placeholder={'nicht gebleedet'} value={list_wheels.gebleedet}
                               name={'gebleedet'} onChange={this.save_changes_wheelSet}/>
                        <input id={list_wheels.fl_id} placeholder={'Bleed FL'} value={list_wheels.fl_bleed_press}
                               name={'bleed_press'} className={'fl_bleed_press'}  onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.fr_id} placeholder={'Bleed FR'} value={list_wheels.fr_bleed_press}
                               name={'bleed_press'} className={'fr_bleed_press'}  onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.bl_id} placeholder={'Bleed BL'} value={list_wheels.bl_bleed_press}
                               name={'bleed_press'} className={'bl_bleed_press'}  onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.br_id} placeholder={'Bleed BR'} value={list_wheels.br_bleed_press}
                               name={'bleed_press'} className={'br_bleed_press'} onChange={this.save_changes_wheel}/>
                    </td>

                    <td style={{border: "solid", borderColor: 'dimgrey', height: 20, width: 150, padding: '8px'}}>
                        <input id={list_wheels.setid} placeholder={'ID FL'} value={list_wheels.fl_id_scan}
                               name={'id_scan'} className={'fl_id_scan'} onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.fr_id} placeholder={'ID FR'} value={list_wheels.fr_id_scan}
                               name={'id_scan'} className={'fr_id_scan'} onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.bl_id} placeholder={'ID BL'} value={list_wheels.bl_id_scan}
                               name={'id_scan'} className={'bl_id_scan'} onChange={this.save_changes_wheel}/>
                        <input id={list_wheels.br_id} placeholder={'ID BR'} value={list_wheels.br_id_scan}
                               name={'id_scan'} className={'br_id_scan'}  onChange={this.save_changes_wheel}/></td>
                </tr>
            )
        })
    }



        render() {
        return (
            <ScrollView >
               <View>
                   <Text style={{fontSize: 30, fontWeight: 'bold', textAlign: 'center'}}>
                   Neue Reifenbestellung anlegen
                   </Text>
               </View>
                <div>
                    <h1 id='title'>Neue Reifenbestellung anlegen</h1>
                    <table id='order'>
                        <tbody>
                        {this.renderTableHeader(1)}
                        {this.renderTableOrderCat()}
                        </tbody>
                    </table>
                </div>
                <div>
                    <h1 id='title'>Ausgewhltes Reifenset bearbeiten</h1>
                    <table id='choosen'>
                        <tbody>
                        {this.renderTableHeader(2)}
                        {this.renderTableData()}
                        </tbody>
                    </table>
                </div>

                <Button
                        title="zurck"
                        onPress={this.changeRace}
                />

            </ScrollView>
        );
    }
}
